mapper.update:
update shopping_carts_commodities
set shopping_cart_id = #{shoppingCartId,jdbcType=INTEGER},
  commodity_id = #{commodityId,jdbcType=INTEGER},
  buy_number = #{buyNumber,jdbcType=INTEGER},
  store_id = #{storeId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, shopping_cart_id, commodity_id, buy_number, store_id
from shopping_carts_commodities
where id = #{id,jdbcType=INTEGER}
